    private void SolvedActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SolvedActionPerformed
        int DAXI = 0,DAYJ = 0,DAZK = 0,DGEscalar = 0;
        int RAI = 0,RAJ = 0,RAK = 0;
        double SMODULO = 0,RRMODULO=0;
        
        String RVectorM="";
        String RModulos="";
        
        DAXI=Integer.parseInt(AXI.getText());
        DAYJ=Integer.parseInt(AYJ.getText());
        DAZK=Integer.parseInt(AZK.getText());
        DGEscalar=Integer.parseInt(GEscalar.getText());
        
        RAI = (DAXI * DGEscalar);
        RAJ = (DAYJ * DGEscalar);
        RAK = (DAZK *DGEscalar );
        
        
        RVectorM="";
                
        if(RAI >= 0){
            RVectorM = DGEscalar+".V= "+"" + RAI + "i";
        }else{
            RVectorM = DGEscalar+".V= "+"" + RAI + "i";
        }
        if(RAJ >= 0){
            RVectorM += "+" + RAJ + "j";
        }else{
            RVectorM += "" + RAJ + "j";
        }
        if(RAK >= 0){
            RVectorM += "+" + RAK + "k";
        }else{
            RVectorM += "" + RAK + "k";
        }
        RVector.setText(RVectorM);
               
        SMODULO = Math.pow(RAI, DGEscalar)+ Math.pow(RAJ, DGEscalar)+ Math.pow(RAK, DGEscalar);                
        RRMODULO = Math.sqrt(SMODULO);
        
        RModulos="" + RRMODULO;       
        
        RModulo.setText(RModulos);
        
    }//GEN-LAST:event_SolvedActionPerformed

